script:
frappe.ui.form.on('Contract', {
    document_name: handleProject,
    document_type: handleProject
})


function handleProject(frm) {

    if (frm.doc.document_type
        && frm.doc.document_type === 'Project'
        && frm.doc.document_name
        && frm.doc.document_name !== '') {
        frm.set_value('project', frm.doc.document_name);
    } else {
        frm.set_value('project', '');
        frm.set_value('master_project', '');
        frm.set_value('document_name', '');
    }

}
